import '../styles/Main.css';
import Bottom from "./Bottom";
import React, { Component } from "react";


class Day extends Component {
  render() {
    const { data } = this.props;
    console.log("data", data);
    if (!data) return null;

    const { dt_txt } = data;
    const { temp } = data.main;
    const { icon, description } = data.weather[0];
    return (
      <div className="day-container">
        <p>{dt_txt}</p>
        <img
          src={`http://openweathermap.org/img/w/${icon}.png`}
          alt="weather"
        />
        <p>{description}</p>
        <strong>{temp} celsius</strong>
      </div>
    );
  }
}



//Constantes
const allinfo = [
    {
        donnee : "donnee1",
        info : "plusinfo1",
    },
    {
        donnee : "donnee2",
        info : "plusinfo2",
    },
    {
        donnee : "donnee3",
        info : "plusinfo3",
    },
    {
        donnee : "donnee4",
        info : "plusinfo4",
    },
];

//Boutons 
function Infoebutton(props){
    return(
      <button className="butts"  onClick={props.onclick} >{props.name} </button>
    )
  }
  
//Class qui gère les données affichées
  class Profile extends React.Component {
  
    render(){
      return(
        <div>
          <div>
            <div>
                <p> Données : {this.props.donnee}</p>
            </div>
          </div>
        </div>
  
      )
    }
  } 

//Affichage de la page
class Main extends React.Component {
    constructor(props){
        super(props);
        //La donnée affichée est la première par défault
        this.state = {
          activeprofile: 0
        }
        this.handleclick = this.handleclick.bind(this);
      } 
      //Effet du click du boutton
      handleclick(index){
        console.log(index)
        this.setState({activeprofile:index})
      }

    //Affichage de la page
    render(){
        return (
            <div class="container">
            
                <div class="row">
                    <h1 className="font">Overview</h1>   
                </div>

                <div class="row">
                    <div className="top">
                    {allinfo.map((profil,index)=>
                    <Infoebutton onclick={()=>this.handleclick(index)} name={profil.donnee} key={index}></Infoebutton>)}
                    </div> 
                </div>

                <div class ="row">
                    <div className="mid">
                        <article>
                        <Profile donnee={allinfo[this.state.activeprofile].donnee} 
                        info={allinfo[this.state.activeprofile].info}>
                        </Profile>
                        </article>
                    </div>
                </div>

                <div class = "row">
                    <Bottom />
                </div>
        
        </div>
    );
}
}

export default Main;
