import React, { Component } from "react";
import "../styles/Container.css";
import "../styles/Main.css"


const API_URL = "https://api.meteo-concept.com/api/forecast/daily/0/periods?";
const API_KEY = "aa6874dd2912cb57dd5e33c70a69fb545ed2ab9c3cd31123c7cb2ebc47db8a74";

class WindAPI extends Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: []
      };
    }
  
    componentDidMount(city) {
      fetch(`${API_URL}${API_KEY}&name=${city}`)
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result['forecast']
            });
          },
          // Remarque : il est important de traiter les erreurs ici
          // au lieu d'utiliser un bloc catch(), pour ne pas passer à la trappe
          // des exceptions provenant de réels bugs du composant.
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }
  
    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Erreur : {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Chargement…</div>;
      } else {
        return (
            <ul>
            {items.map(item => (
              <li key={item.name}>
                {item.name} {item.price}
              </li>
            ))}
          </ul>
        
        );
      }
    }
  }

  export default WindAPI;
